    "    \"is_weekend\",\"is_month_end\",\"is_quarter_end\",\"is_year_end\",\n",
    "    \"days_since_last_txn_debit\",\"days_since_last_txn_credit\"\n",
    "]\n",
    "for c in safe_zero_cols:\n",
    "    if c not in df_ind.columns:\n",
    "        df_ind[c] = 0.0\n",
